    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Contato c = montaContato();
        String resp = new AgendaDAO().gravarContato(c);        
        if(resp.equals("OK")) {
            JOptionPane.showMessageDialog(null, "Contato Gravado com sucesso");
            limparCampos();
            carregarLista();
        }
        else {
            JOptionPane.showMessageDialog(null, "Erro ao gravar.");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        // TODO add your handling code here:
        jButton4.setVisible(true);
        int it = jTable1.getSelectedRow();
        jTextField1.setText(jTable1.getModel().getValueAt(it, 0).toString());
        jTextField2.setText(jTable1.getModel().getValueAt(it, 1).toString());
        jFormattedTextField1.setText(jTable1.getModel().getValueAt(it, 2).toString()); 
    }//GEN-LAST:event_jTable1MouseClicked

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        limparCampos();
        carregarLista();
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
         if (jTable1.getSelectedRow() <0){
            JOptionPane.showMessageDialog(null, "Selecione um registro.");
        }
        else{
            if (new AgendaDAO().delContato(Integer.parseInt(
                                                        jTable1.getModel().getValueAt(
                                                            jTable1.getSelectedRow(), 0).toString()))){            
                JOptionPane.showMessageDialog(null, "Registro excluído.");
                limparCampos();
                carregarLista();
            }
            else{
                JOptionPane.showMessageDialog(null, "Registro não encontrado.");
            }
        } 
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Contato c = montaContato();
        String resp = new AgendaDAO().alterarContato(c);        
        if(resp.equals("OK")) {
            JOptionPane.showMessageDialog(null, "Contato alterado com sucesso");
            limparCampos();
            carregarLista();
        }
        else {
            JOptionPane.showMessageDialog(null, "Erro ao gravar.");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

