    public void carregarLista() {
        DefaultTableModel modelo = (DefaultTableModel) jTable1.getModel();        
        List<Contato> lista = new AgendaDAO().listarContatos();
        modelo.setNumRows(0);
        for (int i = 0; i < lista.size(); i++) {
            modelo.addRow(new String[]{ lista.get(i).getId()+"",
                                        lista.get(i).getNome(), 
                                        lista.get(i).getTelefone()});
        }  
        jTextField1.setText(new AgendaDAO().getLastCodigo()+"");
        
        jButton4.setVisible(false);
    }
    
    public DefaultFormatterFactory setFormatoTel() {             
        try {
            return new DefaultFormatterFactory(new MaskFormatter("(##) ####-####")); 
        } catch (Exception e) {}
        return null;
    }

    public Contato montaContato(){
        Contato c = new Contato();
        c.setId(Integer.parseInt(jTextField1.getText()));
        c.setNome(jTextField2.getText());
        c.setTelefone(jFormattedTextField1.getText());        
        return c;
    }   
    
    public void limparCampos(){
        jTextField1.setText("");
        jTextField2.setText("");
        jFormattedTextField1.setText("");
    }
    
    
